function closeIt(){$.getJSON(wikiAPI,function(e){$.each(e.query.pages,function(e,t){$.getJSON("http://en.wikipedia.org/w/api.php?action=query&prop=info&pageids="+t.pageid+"&inprop=url&format=json&callback=?",function(e){$.each(e.query.pages,function(e,a){$("#res").html('<h2>Random Wikipedia Article</h2><h1><a href="'+a.fullurl+'" target="_blank">'+a.title+"</a></h1><p>"+t.extract+"</p>")})})})})}var wikiAPI="http://en.wikipedia.org/w/api.php?action=query&generator=random&grnnamespace=0&prop=extracts&exchars=250&&explaintext&exintro=&format=json&callback=?";$(document).ready(closeIt),$(".random").click(function(){closeIt(1,!1)}),$("#res").on("swipe",function(){closeIt(1,!1)});